class ServerOperation {

                String operation;
                Date operationDate;
                Long timeTaken;
                String request;
                String response;
                String responseName;
                String uuidSession;
}

class PlatinesPublisher {
    private static final String URL_PLATINES = System.getenv("PLATINES_PUBLISHER");

    public void publish(ServerOperation serverOperation) {
        String url = URL_PLATINES + "/saveServerOperation";
        Gson gson = new Gson();
        MultipartEntityBuilder builder = MultipartEntityBuilder.create();
        builder.addTextBody("operation", gson.toJson(serverOperation),
                ContentType.create("application/json", Consts.UTF_8));
        HttpEntity multipart = builder.build();
        sendRequest(url, multipart);
    }

    private String sendRequest(String url, HttpEntity httpEntity) {
        HttpClient httpClient = HttpClientBuilder.create().build();

        HttpPost post = new HttpPost(url);
        HttpResponse response;
        String responseServer = "";
        try {
            post.setEntity(httpEntity)

            response = httpClient.execute(post);
            BufferedReader br = new BufferedReader(new InputStreamReader(response.getEntity().getContent(), "UTF-8"));
            while ((responseServer = br.readLine()) != null) {
                responseServer += responseServer;
            }
        } catch (IOException e) {
            LOGGER.error("Erreur lors de l'envoi des informations platines", e)
        }
        return responseServer;
    }

}

