boolean docXMLValide(String docAValider, File xsdFile) {
    def docValidity = false;

    //Schema XSD
    def streamXsd = new FileInputStream(xsdFile)

    //Document XML
    def streamXml = new ByteArrayInputStream(docAValider.getBytes())
    def streamSourceXml = null
    try {
        //Validation
        streamSourceXml = new StreamSource( streamXml )
        SchemaFactory.newInstance( XMLConstants.W3C_XML_SCHEMA_NS_URI )
                .newSchema( new StreamSource( streamXsd ) )
                .newValidator()
                .validate( streamSourceXml )
        log.info("Schéma valide")
        docValidity = true;
    } catch (Exception e) {
        log.info('Erreur lors de la validation du fichier: ' , e)
    } finally{
        streamSourceXml.inputStream.close()
    }
    return docValidity;
}

String getOriginalDoc(GroovyUtils groovyUtils) {

    def originalXml = null
    def originalStr = null
    try  {
        originalXml = groovyUtils.getXmlHolder(mockRequest.requestContent)
    } catch (Exception e) {
        context.listeErreursLib =""
        log.info('Erreur format XML : ' , e)
        context.reponseRenvoyee="Erreur_400"
        return "Erreur_400"
    }

    // Retrieving the original document depends on the type of signature (enveloping/enveloped)
    def EncodedOriginalDoc = originalXml.getNodeValue("//*:Object[contains(@Id,'o-id')]")

    if ( EncodedOriginalDoc != null) {                      // Enveloping signature
        def byte[] decoded = EncodedOriginalDoc.decodeBase64()
        originalStr = new String(decoded)
    } else {                                               // Enveloped signature
        originalXml.removeDomNodes("//*:Signature")
        originalXml.updateProperty()
        originalStr = originalXml.getXml()
    }

    return originalStr
}

