    // Validation de la signature contenue dans la requête
    RapportSignature rs = svs.validateSignature(requete)

    if ( rs.isValide() ){

        context.listeErreursLib =""

        // Test 1 : Validation de la syntaxe du fichier de demande XML

        // Chargement du fichier XSD référencé dans la variable schemaxsdLocal (niveau Projet)
        projectDir = groovyUtils.projectPath
        schemaxsdLocal = groovyUtils.expand( '${#Project#schemaxsdLocal}' )
        xsdFile = new File(projectDir + File.separator + schemaxsdLocal)
        log.info("xsdFile : " + schemaxsdLocal)

        if (!docXMLValide(strOriginal, xsdFile)) {
            context.reponseRenvoyee="Erreur_400"
            return "Erreur_400"
        }

