        // Test 2 : Vérification de la cohérence entre l'émetteur de la demande nommé dans la balise "EtabEmetteur"
        // et la valeur du DN du certificat de la signature

        def idEmettFic=docOriginal.getNodeValue("//*:TOMF/@IdEmettFic")
        def identStructure=docOriginal.getNodeValue("//*:EtabEmetteur/*:IdentifiantStructure/text()")
        def identStructureAvecType=docOriginal.getNodeValue("concat(//*:EtabEmetteur/*:TypeStructure/text(),//*:EtabEmetteur/*:IdentifiantStructure/text())")

        // Récupération de l'OU du DN du certificat via la lib
        log.info( "OU SIGNATAIRE : " + svs.getCertificatEmetteur())
        def String OUSignataire = svs.getCertificatEmetteur()

        if (identStructureAvecType.equals(OUSignataire) && identStructure.equals(idEmettFic) ) {
            log.info "Contrôle EtabEmetteur OK -- Valeurs comparées :  "
            log.info "identStructureAvecType : " + identStructureAvecType +  " et OUSignataire : " + OUSignataire
            log.info "IdentifiantStructure : " + identStructure + " et IdEmettFic  : "+ idEmettFic

            // Contrôle unicité de l'identifiant emetteur
            def idLotEmett = docOriginal.getNodeValue("//*:TOMF/@IdLotEmett")
            def propertyIdLot = mockOperation.mockService.project.getPropertyValue("IdLotEmetteurEnregistre")

            if (idLotEmett != null && idLotEmett.equals(propertyIdLot)) {
                log.info "Controle unicité de l'IdLot KO -- Valeurs comparées : "
                log.info  "idLotEmett :"  + idLotEmett
                log.info "propertyIdLot :"  + propertyIdLot
                context.reponseRenvoyee="Erreur_409"
                return "Erreur_409"
            }
            context.reponseRenvoyee="Succes_202"
            return "Succes_202"
        } else{
            log.info "Contrôle EtabEmetteur KO -- Valeurs comparées :  "
            log.info "identStructureAvecType : " + identStructureAvecType +  " et OUSignataire : " + OUSignataire
            log.info "IdentifiantStructure : " + identStructure + " et IdEmettFic : "+ idEmettFic
            context.reponseRenvoyee="Erreur_403_incoherence_signataire"
            return "Erreur_403_incoherence_signataire"
        }
    } else {

        // Récupération des 1ers codes, messages et causes des erreurs
        String code_lib = rs.getDataListSignature().get(0).getCodeSignature()
        String message_lib = rs.getDataListSignature().get(0).getMessageSignature()
        String cause_lib = rs.getDataListSignature().get(0).getCause()

        // Pour les erreurs E01_TWS, on utilise le code d'erreur ERSIGN (présent dans le message) comme nom de réponse, pour les autres, le code E0X
        if (code_lib.equals("E01_TWS")){
            String reponseRenvoyee = message_lib.substring(0, message_lib.lastIndexOf(": "))
            context.reponseRenvoyee=reponseRenvoyee
            requestContext.message_lib = message_lib.substring(message_lib.lastIndexOf("ERSIGN")+10)
